import java.util.*;

public class circle {
    public static void main(Strings [] args){
        Scanner sc = new Scanner(System.in);
        int radius;
        double pi = 3.14, area;
        //Scanner sc = new Scanner(System.in);
        System.out.print("Enter radius of circle: ");
        radius = sc.nextInt();
        area = pi * radius * radius;
        System.out.println("Area of circle: "+area);
    }            
}
    
